package new_java;

public class hellow_world {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hellow_world  this is radhika");
		
		int a = 1;
		for(int i=1; i<5; i++) {
		//	System.out.println("1 outer loop");
			for(int j=1; j<=i; j++) {
				System.out.print(a);
				System.out.print(" ");
				a++;
				
			}
			System.out.println("");
			//System.out.println("2 outer loop");
		}
		
	}

}
